public class LinkedListStudy {    Node header;    class Node {        int data;        Node next = null;    }    void append(int d) {        Node end = new Node();        end.data = d;        Node n = header;        while (n.next != null) {            n = n.next;        }        n.next = end;    }    void delete(int d) {        Node n = header;        while (n.next != null) {            if (n.next.data == d) {                n.next = n.next.next;            } else {                n = n.next;            }        }    }    void retreive() {        Node n = header.next;        while (n.next != null) {            System.out.print(n.data + "->");            n = n.next;        }        System.out.println(n.data);    }    LinkedListStudy() {        header = new Node();    }}class LinkedListNode {    public static void main(String[] args) {        LinkedListStudy lls = new LinkedListStudy();        lls.append(1);        lls.append(2);        lls.append(3);        lls.append(4);        lls.retreive();        lls.delete(1);        lls.retreive();    }}