package stream;import java.util.Arrays;import java.util.List;import java.util.Map;import java.util.stream.Collectors;public class GroupingByExample {    public static void main(String[] args) {        List<Student> totalList = Arrays.asList(                new Student("홍길동", 10, Student.SEX.MALE, Student.City.Seoul),                new Student("백승한", 5, Student.SEX.MALE, Student.City.Suwon),                new Student("신용권", 10, Student.SEX.MALE, Student.City.Seoul),                new Student("김애리", 5, Student.SEX.FEMALE, Student.City.Suwon)        );        Map<Student.SEX, List<Student>> mapBySex = totalList.stream()                .collect(Collectors.groupingBy(Student::getSEX));        System.out.println("남학생");        mapBySex.get(Student.SEX.MALE).stream()                .forEach(s -> System.out.print(s.getName() + " "));        System.out.println("여학생");        mapBySex.get(Student.SEX.FEMALE).stream()                .forEach(s -> System.out.print(s.getName() + " "));        System.out.println();        Map<Student.City, List<String>> mapByCity = totalList.stream()                .collect(                        Collectors.groupingBy(                                Student::getCity,                                Collectors.mapping(Student::getName, Collectors.toList())                        )                );        System.out.println("\n서울");        mapByCity.get(Student.City.Seoul)                .forEach(c -> System.out.print(c + " "));        System.out.println("\n수원");        mapByCity.get(Student.City.Suwon)                .forEach(c -> System.out.print(c + " "));    }}