package stream;import java.util.Arrays;import java.util.List;import java.util.stream.Stream;public class LambdaExpressionExample {    public static void main(String[] args) {        List<Student> list = Arrays.asList(                new Student("cho", 25, Student.SEX.MALE),                new Student("baek", 25, Student.SEX.MALE),                new Student("weon", 25, Student.SEX.MALE)        );        Stream<Student> stream = list.stream();        stream.forEach( s -> {            String name = s.getName();            int score = s.getScore();            System.out.println("이름 : " + name + ", 점수 : " + score);        });    }}