package stream;import java.util.Arrays;import java.util.List;public class MapAndReduceExample {    public static void main(String[] args) {        List<Student> studentList = Arrays.asList(                new Student("baek", 70),                new Student("cho", 58),                new Student("weon", 80)        );        double avg = studentList.stream()                .mapToInt(Student::getScore)                .average()                .getAsDouble();        System.out.println("평균 점수 : " + avg);    }}