package stream;import java.util.Arrays;import java.util.List;import java.util.stream.Stream;public class StreamPipelinesExample {    public static void main(String[] args) {        List<Member> list = Arrays.asList(                new Member("kildong", Member.MALE, 25),                new Member("baek", Member.MALE, 45),                new Member("weon", Member.FEMALE, 27)        );        double ageAvg = list.stream()                .filter(a -> a.getSex() == Member.MALE)                .mapToInt(Member::getAge)                .average()                .getAsDouble();        System.out.println(ageAvg);    }}