package stream;public class Student implements Comparable<Student> {    public enum SEX  {MALE, FEMALE}    private String name;    private int score;    private SEX sex;    public Student(String name, int score, SEX sex) {        this.name = name;        this.score = score;        this.sex = sex;    }    public String getName() {        return name;    }    public int getScore() {        return score;    }    public SEX getSEX() {return sex;}    @Override    public int compareTo(Student o) {        return Integer.compare(score, o.score);    }}