package stream;public class Student implements Comparable<Student> {    public enum City {Seoul, Suwon}    public enum SEX  {MALE, FEMALE}    private String name;    private int score;    private SEX sex;    private City city;    public Student(String name, int score, SEX sex, City city) {        this.name = name;        this.score = score;        this.sex = sex;        this.city = city;    }    public String getName() {        return name;    }    public int getScore() {        return score;    }    public SEX getSEX() {return sex;}    public City getCity() {return city;}    @Override    public int compareTo(Student o) {        return Integer.compare(score, o.score);    }}