package week_10.waitAndNotify;public class ThreadWaitExample_1 {    public static void main(String[] args) {        Table table = new Table();        new Thread(new Cook(table), "COOK1");        new Thread(new Customer(table, "donut"), "CUST1").start();        new Thread(new Customer(table, "burger"), "CUST2").start();        try {            Thread.sleep(100);            System.exit(0);        } catch (Exception e) {        }    }}