package week_11;public enum NewTransportation {    BUS(100) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, TRAIN(150) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, SHIP(100) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, AIRPLANE(300) {        int fare(int distance) {            return distance * BASIC_FARE;        }    };    protected final int BASIC_FARE;    NewTransportation(int BASIC_FARE) {        this.BASIC_FARE = BASIC_FARE;    }    abstract int fare(int distance);    public int getBASIC_FARE() {        return BASIC_FARE;    }}class Enumex3 {    public static void main(String[] args) {        System.out.println("Bus fare : " + NewTransportation.BUS.fare(100));        System.out.println("Train fare : " + NewTransportation.TRAIN.fare(100));        System.out.println("Ship fare : " + NewTransportation.SHIP.fare(100));        System.out.println("Airplane fare : " + NewTransportation.AIRPLANE.fare(100));    }}