package week_11;public enum NewTransportation {    BUS(100) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, TRAIN(150) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, SHIP(100) {        int fare(int distance) {            return distance * BASIC_FARE;        }    }, AIRPLANE(300) {        int fare(int distance) {            return distance * BASIC_FARE;        }    };    abstract int fare(int distance);    protected final int BASIC_FARE;    NewTransportation(int BASIC_FARE) {        this.BASIC_FARE = BASIC_FARE;    }    public int getBASIC_FARE() {        return BASIC_FARE;    }}