package Main;public class OddException extends RuntimeException {    public OddException() {    }    public OddException(String message) {        super(message);    }    public OddException(String message, Throwable cause) {        super(message, cause);    }    public OddException(Throwable cause) {        super(cause);    }    public OddException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);    }}