package stackAndQueue;import java.util.LinkedList;import java.util.Queue;public class QueueExam {    public static void main(String[] args) {        Queue<Message> messages = new LinkedList<>();        messages.offer(new Message("메일 보내기", "승한"));        messages.offer(new Message("문자 보내기", "승훈"));        messages.offer(new Message("파일 전송", "승백"));        while (!messages.isEmpty()) {            Message message = messages.poll();            System.out.println(message.to + "님에게 " + message.command + "(을)를 합니다");        }    }}