package stream;import java.util.Arrays;import java.util.List;public class MapExample {    public static void main(String[] args) {        List<Student> studentList = Arrays.asList(                new Student("홍길동", 10, Student.SEX.MALE),                new Student("백승한", 20, Student.SEX.MALE),                new Student("김말이", 30, Student.SEX.MALE)        );        studentList.stream()                .mapToInt(Student::getScore)                .forEach(score -> System.out.println(score));    }}