package stream;import java.util.Arrays;import java.util.List;public class ReductionExample {    public static void main(String[] args) {        List<Student> studentList = Arrays.asList(                new Student("홍길동", 25, Student.SEX.MALE),                new Student("백승한", 30, Student.SEX.MALE),                new Student("신용권", 40, Student.SEX.MALE)        );        int sum1 = studentList.stream()                .mapToInt(Student::getScore)                .sum();        int sum2 = studentList.stream()                .mapToInt(Student::getScore)                .reduce((a, b) -> a + b)                .getAsInt();        int sum3 = studentList.stream()                .mapToInt(Student::getScore)                .reduce(0, (a, b) -> a + b);        System.out.println("Sum 1 : " + sum1);        System.out.println("Sum 2 : " + sum2);        System.out.println("Sum 3 : " + sum3);    }}