package week_10.priority;public class ThreadPriorityExample {    public static void main(String[] args) {        ThreadPriorityExample_1 t1 = new ThreadPriorityExample_1();        ThreadPriorityExample_2 t2 = new ThreadPriorityExample_2();        t2.setPriority(7);        System.out.println("Priority of t1 (-) : " + t1.getPriority());        System.out.println("Priority of t2 (|) : " + t2.getPriority());        t1.start();        t2.start();    }}class ThreadPriorityExample_1 extends Thread {    @Override    public void run() {        for (int i = 0; i < 300; i++) {            System.out.print("-");            for (int x = 0; x < 10_000_000; x++) ;        }    }}class ThreadPriorityExample_2 extends Thread {    @Override    public void run() {        for (int i = 0; i < 300; i++) {            System.out.print("|");            for (int x = 0; x < 10_000_000; x++) ;        }    }}