package switch13;public class SwitchExam {    public enum Month {JAN, FEB, MAR, APR, MAY, JUN, JUL, AUG, NOV, OCT, SEP, DEC;}    public static void main(String[] args) {        int day = 0;        Month month = Month.APR;        switch (month) {            case JAN:            case MAR:            case MAY:            case JUL:            case AUG:            case DEC:                day = 31;                break;            case APR:            case JUN:            case SEP:            case NOV:            case OCT:        }    }}